<script>
	import {
		mapState
	} from "vuex"
	import popularity from "../components/popularity.vue"
	export default {
		props: ['banners', 'recommendData', 'dailyList'],
		data() {
			return {
				currentIndex: 1,
				setNumber: 0,
				id: ''
			}
		},
		computed: {
			...mapState(['historyComic']),
			firstHistory() {
				return this.historyComic.filter((t, i) => i == 0)
			}
		},
		methods: {
			swierChange(e) {
				this.currentIndex = e.detail.current
			},
			changeList() {
				// console.log(1);
				this.$emit('changeList')
			},
			goTodetail(a) {
				uni.navigateTo({
					url: '../detail/detail?detail_id=' + a
				});
			},
			goToRead(a) {
				uni.navigateTo({
					url: '../reader/reader?comics_id=' + a
				});
			},
			goToDaily() {
				uni.navigateTo({
					url: '../daily/daily'
				});
			},
			goToLeader() {
				uni.navigateTo({
					url: '../leaderboard/leaderboard'
				});
			}
		},
		components:{
			popularity
		}

	}
</script>
